package leetcode.project;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.SortedSet;
import java.util.TreeSet;

public class FindAnagrams {
	public static List<Integer> findAnagrams(String s, String p) {
		List<Integer> result = new ArrayList<>();
		int[] arr = new int[26];
		for (int i = 0; i < p.length(); i++) {
			arr[p.charAt(i) - 'a']++;
		}

		int start = 0;
		int end = 0;
		while (end < s.length()) {
			if (arr[s.charAt(end) - 'a'] > 0) {
				arr[s.charAt(end++) - 'a']--;
				if (end - start == p.length())
					result.add(start);
			} else if (start == end) {
				start++;
				end++;
			} else {
				arr[s.charAt(start++) - 'a']++;
			}
		}

		return result;
	}

	public static List<Integer> findAnagrams1(String s, String p) {
		char[] pChar = p.toCharArray();
		Arrays.sort(pChar);
		p = new String(pChar);
		SortedSet<Integer> set = new TreeSet<>();
		for (int i = 0; i < p.length(); i++) {
			char ch = p.charAt(i);
			int startIndex = 0;
			int index = 0;
			while (true) {
				index = s.indexOf(ch, startIndex);
				if (index == -1 || index > s.length() - p.length())
					break;
				String str = s.substring(index, index + p.length());
				char[] sChar = str.toCharArray();
				Arrays.sort(sChar);
				String pStr = new String(sChar);
				if (pStr.equalsIgnoreCase(p))
					set.add(index);
				startIndex = index + 1;
			}
		}
		List<Integer> list = new ArrayList<>();
		for (Integer data : set) {
			list.add(data);
		}
		return list;
	}

	public static void main(String[] args) {
		String str = "";
		String p = "";
//		String str = "aaaaabaaaaa";
//		String p = "aaaaa";
		System.out.println(findAnagrams(str, p));
		// System.out.println('b' - 'a');

	}

}
